from Crypto.Util.number import bytes_to_long, getPrime
from random import randint

flag = b'sstctf{REDACTED}'


x1 = getPrime(512)
x2 = getPrime(512)

assert x1 > x2

while True:
    a = randint(1,2**1024-1)
    b = randint(1,2**1024-1)
    c = randint(1,2**1024-1)

    if a*(x1)**2+b*(x1)+c == a*(x2)**2+b*(x2)+c == 0:
        break

# if you're sec 2, open a ticket and ping me @Water, I'll give you a hint

ct = bytes_to_long(flag)+x1*x2

print('a =',a)
print('b =',b)
print('c =',c)
print('ct =',ct)

